//MOSTRAR EL IDIOMA DE LOS BOTONES DE CORREGIR Y SOLUCIÓN
$(".btn-solucion").html(btnSolucion);
$(".btn-corregir").html(btnCorregir);
$(".btn-reiniciar").html(btnReiniciar);

//MOSTRAR EL IDIOMA DE LAS AYUDAS, RECURSOS Y LOS TOOLTIPS DEL CURSO

$(".pAudioPrincipal").html(audioprincipalayuda);
$(".pAudioSecundario").html(audioprincipalayuda);
$(".pAyuda").html(recursosayuda);

//--------------------------------------------------------------------------------------------//
//--------------------------------------------------------------------------------------------//
//--------------------------------------------------------------------------------------------//

//Ocultar el botón de Solución de inicio en las autoevaluaciones
$(".btn-solucion").css("display", "none");


//Pintar la viñeta de las listas desordenadas
$('.lista li').before($square);

//Pintar los botones del carousel (Next y Prev)
$('.carousel-prev').html($prev);
$('.carousel-next').html($next);


//Acordeón Icono
$('.accordion-toggle').prepend($mas);

//Acordeón
/*$acordeonActivo=true;
$(".accordion-toggle").on('click', function(){
	if($acordeonActivo){
		$acordeonActivo.addClass('collapsed');
	}
	$acordeonActivo=$(this); 		
});*/

//Imagen dinámica
$('.grupo-img').on('click', function(){

	$imgBtn=$(this).attr('data-id');
	//Eliminamos todas las clases activo
	$('.contenedor-img-btns').find('.grupo-img').each(function(){
		$('.grupo-img').removeClass('activo');
	});
	$('.contenedor-txt-btns').css('display', 'inline-block');
	//Mostramos el texto correspondiente
	$textoBtn=$(".grupo-img-text .text"+$imgBtn).html();
	$('.contenedor-txt-btns').html($textoBtn);

	//Añadimos la clase correspondiente al texto selecionado por el botón
	$(".grupo-img[data-id='"+$imgBtn+"']").addClass('activo');
});

//Ponemos título para la versión del móvil
$size = $(".grupo-img").size();
for(i=1; i<=$size; i++){	
	$tituloImagenBtn = $("[data-id='"+i+"'] > div > h6").html();
	$(".contenedor-img-btns .text" + i).prepend('<h6>'+$tituloImagenBtn+'</h6>');
}

//Esto lo hacemos para ver como es de largo es el título y aplicar la clase correspondiente
//Contamos cuantos elementos tenemos
$tituloLargo = 0;
for(i=1; i<=$size; i++){
	//Miramos cuantos caracteres tenemos
	$string=$(".contenedor-img-btns [data-id='"+i+"'] h6").html();
	$numLetras = $string.length;
	if($numLetras > $tituloLargo){
		$tituloLargo = $numLetras;
	}
}

//Ponemos la altura
if($tituloLargo > 40){
	$(".grupo-img").css('height', '234px');
	$(".grupo-img > .titulo-imagen").css('height', '71px');
}else if($tituloLargo > 20){
	$(".grupo-img").css('height', '217px');
	$(".grupo-img > .titulo-imagen").css('height', '54px');
}

//Para que al pinchar en las imágenes dinámicas se haga un scroll suave automático al contenido que se ha desplegado
if ($(".contenedor-img-btns").length == 1) { //Para que se corra solo en las páginas en donde hay imágenes dinámicas
    $(".grupo-img-text").on("click", function(){
        if (window.matchMedia("(min-width: 769px)").matches) { //Para desactivarlo en versión móvil
            $('html, body').animate({
                scrollTop: $(".contenedor-txt-btns").offset().top - $(window).height() + $(".contenedor-txt-btns").height() + $("footer").height() + 50 //Calculo del punto justo de scroll
            }, 1000);    ;
        }
    });
}

