var $item;
var active,a;
var cpy;


$(function(){
	initComponents();
})

function initComponents(){
	//Selecionamos los objetos
	$item = $('.item');
	//Añadimos a todos los elementos item los siguientes eventos
	$.each($item,function(i){
		this.ondragstart = inicia;
		this.ondragleave = fin;
		this.ondragover = encima;
		this.ondrop = suelta;
	});
}

function inicia(e){
	//Indicamos al usuario con el cursor que solo podra mover ese objeto
	e.dataTransfer.effectAllowed = 'move'
	//Este es objecto arrastrado
	var self = $(this);
	//Nos permite obtener el índice del elemento que estamos arrastrando
	active = self.index();
	//Le enviamos el indice del evento el cual provengo
	e.dataTransfer.setData('i',active);
	//Clonamos el objeto
	cpy = self.clone();
}

function suelta(e){

	//Evitar que se redireccione la página (Firefox)
	e.preventDefault();
	//Me va a impedir que se recargue la imagen
	e.stopPropagation();

	var self = $(this);
	//Teno el indice sobre el elemento que estamos soltando
	var sIndex = self.index();
	//Tenemos el elemnto arrastrado
	var prev = parseInt(e.dataTransfer.getData('i'));

	self.removeClass('item-hover');

	//Si es el mismo objeto del que provengo, no hago nada
	if(sIndex == prev){return false;}

	//Eliminamos el objeto del que provemos
	$item.eq(prev).remove();

	if(prev < sIndex){
		//Lo insertamos antes
		cpy.insertAfter($item.eq(sIndex))
	}else if(prev > sIndex){
		//Lo insertamos despúes
		cpy.insertBefore($item.eq(sIndex))
	}

	//Si se mueve despues de corregir
	$('.ejercicioOrdenar .listaOrdenar .item').removeClass('incorrecto');
	$('.ejercicioOrdenar .listaOrdenar .item').removeClass('correcto');
	$('.ejercicioOrdenar .listaOrdenar .item svg').remove(); 

	//Añadimos los eventos al objeto clonado
	initComponents();
}

function encima(e){

	e.dataTransfer.dropEffect = 'move';

	var self  = $(this);
	if(self.index() === active){return false;}

	//Añadimos la clase, esta clase se pondra en el objeto que choque con nuestro objeto arrastrado
	self.addClass('item-hover');
	return false;
}

function entra(e){
	return false;
}

function fin(e){
	var self = $(this);
	self.removeClass('item-hover');
}

