//Función para ajustar el texto dentro de las cajas del ejercicio ordenar dependiendo de la cantidad de texto que se inserte en ellas. 
	function ajustarTextoOrdenar(){
		//Variable donde se guardará el largo del texto mas largo en las cajas, mas adelante se usa tambien para transformar este valor en un número de pisxeles para la fuente del texto. 
		var $largoTextoEO = 0;
		// Ciclo que obtiene el largo de la cadena de texto mas larga de las cajas.    
		for (var i = 0; i < $(".listaOrdenar>li").length; i++) {
			if ($(".listaOrdenar>li")[i].getElementsByTagName("p")[0].textContent.length > $largoTextoEO) {
				$largoTextoEO = $(".listaOrdenar>li")[i].getElementsByTagName("p")[0].textContent.length;
			}
		}
		// Se transforma el valor obtenido en un número que se puede usar como número de píxeles en la fuente, el valor 1600 se ha ajustado a través  de prueba y error.
		$largoTextoEO = parseInt(1600/$largoTextoEO);
		// Se defíne un tamaño mínimo y máximo para la fuente.
		if ($largoTextoEO<12) {$largoTextoEO=12}
		if ($largoTextoEO>20) {$largoTextoEO=20}
		// Se ajusta la fuente del texto añadiendo un 0.5% del ancho de pantalla para que se vea mejor en pantallas grandes. 	
	    $(".listaOrdenar>li>p").css("font-size", 'calc('+$largoTextoEO+'px + 0.5vw)'); 
	}
	// Se llama a la función de ajustar el texto de la actividad Ordenar
	if ($(".ejercicioOrdenar").length > 0) {ajustarTextoOrdenar();} 

