//SI CLICA EN ALGUNA DE LAS IMÁGENES, SE ACTIVA EL AUDIO PERTENECIENTE A ESA IMAGEN
function pincharImgDinamica(){
	if($(this).parent().siblings(".inst-dinamicimg").length==1){
		//SI ESTOY EN RESOLUCIÓN A PARTIR DE TABLET HACIA ABAJO ME OCULTA EL TEXTO DE LA INSTRUCCIÓN CUANDO PINCHO EN LA IMAGEN CORRESPONDIENTE
		if (window.matchMedia("(max-width: 769px)").matches) {
			textoinstruccion= $(this).parent().siblings(".inst-dinamicimg").css("opacity", "0");
			setTimeout(function(){
				textoinstruccion.css("display", "none");
			}, 700);
		}
	}
	audioimagenclicada=$(".contenedor-img-btns .text"+$(this).parent().attr("data-id")+" audio")[0];
	//SI LA IMAGEN QUE CLICO TIENE AUDIO DENTRO ENTRA AQUÍ
	if(audioimagenclicada!=undefined){
		//PARO TODOS LOS AUDIOS
		for (i=0; i<$("audio").length; i++){
			$("audio")[i].pause();
			$("audio")[i].currentTime = 0;
			$('audio').parent()[i].classList.remove("playing");
		}
		//PARO TODOS LOS VIDEOS
		for(i=0; i<$('video').length; i++){
			if($("video")[i].currentTime !=0){
				$('video')[i].currentTime = 0;
				$('video')[i].pause();
			}
		}
		//REPRODUZCO EL AUDIO DE ESA IMAGEN DINÁMICA
		audioimagenclicada.play();
	}
	//SI LA IMAGEN QUE CLICO NO TIENE AUDIO DENTRO ENTRA AQUÍ
	if(audioimagenclicada==undefined){
		for (i=0; i<$("audio").length; i++){
			//PARA TODOS LOS AUDIOS MENOS EL AUDIO DE INICIO
			if ($("audio")[i].classList.contains("audioinicio") != true){
				$("audio")[i].pause();
				$("audio")[i].currentTime = 0;
			}
		}
		//PARO TODOS LOS VIDEOS
		for(i=0; i<$('video').length; i++){
			if($("video")[i].currentTime !=0){
				$('video')[i].currentTime = 0;
				$('video')[i].pause();
			}
		}
	}
}

